import React, { useEffect, useState }  from 'react';
import {Button, Transfer, Form, Divider} from "antd";
import axios from "axios";
import API_URL from "../conf/api-url";
import "./index.css"


export function KoreanBookPage(){
    const [genres, setGenres] = useState(null);
    const [btnActive, setBtnActive] = useState([]);

    useEffect(()=>{
        axios
        .get(`${API_URL}/korean`)
        .then((result)=>{
            console.log(result);
            setGenres(result.data);

            setBtnActive();
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])

    const toggleActive = (e) => {
        setBtnActive(()=>{
            return e.target.value;
        });
    };

    const Submit = (values)=>{
        axios.post(`${API_URL}/korean`,{
            selected_genres: values
        })
    }

    if (genres === null){
        return( 
            <div id="loading">
                <Button
                type="link"
                loading="true"
                size="large"
                style={{fontSize:"250%"}}>
                    Loading
                </Button>
            </div>
        )
    }

    return(
        <div>
            <Form name="user profile" onFinish={Submit}>
                <div id="mainGenres">
                    {genres["reprGenres"].map((genr, idx)=>{
                        return(
                        <div id="koreanButton"> 
                            <Button
                                type="primary"
                                shape="round"
                                size="large"
                                ghost="true"
                                className={"btn"+(idx==btnActive ? "active":"")}
                                onClick={toggleActive(idx)}
                            >
                                {genr}
                            </Button>
                        </div>
                        );
                    })}
                </div>

                <div id="transfer">
                    <Transfer 
                        dataSource={genres["othersGenres"]}
                        showSearch="true"
                    />
                </div>
            </Form>
        </div>
    )
}
