    //     setNum(newArr);
    //   }

    // const addNum = (idx) => {
    //     if(num[idx]===false){
    //         console.log("hello")
    //         let newArr = Array.from(num);
    //         newArr[idx] = true;
    //         console.log(newArr[0]);
    //         // console.log("newArray: "+{newArr});
    //         setNum(newArr);
    //     }
    //     else{
    //         console.log("world");
            // let newArr = Array.from(num);
            // // console.log("newArray: "+{newArr});
            // newArr[idx] = false;
            // console.log(newArr[0]);
    //         // console.log("newArray: "+{newArr});
    //         setNum(newArr);
    //     }
    // }
    // function minusNum(idx){
    //     setNum(num-1);
    // }

    useEffect(()=>{
        axios
        .get(`${API_URL}/korean`)
        .then((result)=>{
            setGenres(result.data);
            const temp = result.data["reprGenres"];
            var activeArray = []
            for(let i=0; i<temp.length; i++){
                activeArray.push(false)
            }
            setBtnActive(activeArray);
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])

    // console.log(genres)
    // console.log(btnActive)

    function toggleActive(idx){
        if(btnActive[idx] === true){
            console.log("hello");
            let newArr = Array.from(num);
            newArr[idx] = false;
            setBtnActive(newArr);
        }
        else{
            console.log("world");
            let newArr = Array.from(num);
            newArr[idx] = true;
            setBtnActive(btnActive);
        }
    };

    const Submit = (values)=>{
        axios.post(`${API_URL}/korean`,{
            selected_genres: values
        })
    };

    if (genres === null){
        return( 
            <div id="loading">
                <Button
                    type="link"
                    loading="true"
                    size="large"
                    style={{fontSize:"250%"}}
                >
                    Loading
                </Button>
            </div>
        )
    }

    return(
        <div>
            <Form name="user profile" onFinish={Submit}>
                <div id="mainGenres">
                    {genres["reprGenres"].map((genre, idx)=>{
                        return(
                            <Button
                                // key={idx}
                                type="primary"
                                shape="round"
                                size="large"
                                ghost="true"
                                className={"btn" + (btnActive[idx]?"active":"")}
                                onClick={()=>toggleActive(idx)}
                            >
                                {"btn" + (btnActive[idx]?"active":"")}
                                {genre}
                            </Button>
                        );
                    })}
                </div>

                <div id="transfer">
                    <Transfer 
                        dataSource={genres["othersGenres"]}
                        showSearch="true"
                    />
                </div>
            </Form>
        </div>
    )
}
