import React, { useEffect, useState }  from 'react';
import {Button, Transfer, Form, Divider} from "antd";
import axios from "axios";
import API_URL from "../conf/api-url";
import "./index.css"


export function KoreanBookPage(){
    const [genres, setGenres] = useState(null);
    const [btnActive, setBtnActive] = useState(null);
    const [num, setNum] = useState([]);


    const addNum = index => e => {
        console.log('index: ' + index);
        console.log('property name: '+ e.target.name);
        let newArr = [...num]; // copying the old datas array
        // a deep copy is not needed as we are overriding the whole object below, and not setting a property of it. this does not mutate the state.
        newArr[index] = e.target.value; // replace e.target.value with whatever you want to change it to

        setData(newArr);
      }

    // function addNum(idx){

    //     if(num[idx]==false){
    //         console.log("hello");
    //         setNum((arr) => arr[idx]=true);
    //     }
    //     else{
    //         console.log("world");
    //         setNum((arr) => arr[idx]=false);
    //     }
    // }
    // function minusNum(idx){
    //     setNum(num-1);
    // }
    console.log("array num: "+num)
    useEffect(()=>{
        setNum([false,false,false]);
        axios
        .get(`${API_URL}/korean`)
        .then((result)=>{
            setGenres(result.data);
            // const temp = result.data["reprGenres"];
            // var activeArray = []
            // for(let i=0; i<temp.length; i++){
            //     activeArray.push(false)
            // }
            // setBtnActive(activeArray);
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])

    // console.log(genres)
    // console.log(btnActive)

    // function toggleActive(idx){
    //     if(btnActive[idx] === true){
    //         console.log("hello")
    //         btnActive[idx] = false;
    //         setBtnActive(btnActive);
    //     }
    //     else{
    //         btnActive[idx] = true;
    //         console.log("world")
    //         setBtnActive(btnActive);
    //     }
    // };

    const Submit = (values)=>{
        axios.post(`${API_URL}/korean`,{
            selected_genres: values
        })
    };

    if (genres === null){
        return( 
            <div id="loading">
                <Button
                    type="link"
                    loading="true"
                    size="large"
                    style={{fontSize:"250%"}}
                >
                    Loading
                </Button>
            </div>
        )
    }

    return(
        <div>
            <h3>
                {num[0].toString()}
            </h3>
            <Button onClick={addNum}>plus 1</Button>
            {/* <Button onClick={minusNum}>minus 1</Button> */}
            <Form name="user profile" onFinish={Submit}>
                <div id="mainGenres">
                    {genres["reprGenres"].map((genr, idx)=>{
                        return(
                            <Button
                                key={idx}
                                type="primary"
                                shape="round"
                                size="large"
                                ghost="true"
                                // className={"btn" + (btnActive[idx]?"active":"")}
                                // onClick={toggleActive(idx)}
                            >
                                {genr}
                            </Button>
                        );
                    })}
                </div>

                <div id="transfer">
                    <Transfer 
                        dataSource={genres["othersGenres"]}
                        showSearch="true"
                    />
                </div>
            </Form>
        </div>
    )
}
