import React, { useEffect, useState }  from 'react';
import {Button, Transfer} from "antd";
import axios from "axios";
import API_URL from "../conf/api-url";
import "./index.css"

class myButton extends Button{
    constructor(){
        super();
    }

    changeColor(){
        if(this.ghost=="true"){
            this.ghost="false";
        }
        else{
            this.ghost="true";
        }
    }
}


export function KoreanBookPage(){
    const [genres, setGenres] = useState(null);
    useEffect(()=>{
        axios
        .get("http://localhost:8080/korean")
        .then((result)=>{
            console.log(result);
            setGenres(result.data);
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])

    if (genres === null){
        return( 
            <div id="loading">
                <Button
                type="link"
                loading="true"
                size="large"
                style={{fontSize:"250%"}}>
                    Loading
                </Button>
            </div>
        )
    }

    // function genreClick(){
    //     ghost=="false"?(ghost="true"):(ghost="false")
    // }

    return(
    <div>
        <div id="mainGenres">
            {genres["reprGenres"].map((genr)=>{
                return(
                    <div id="koreanButton">
                        <Button
                        type="primary"
                        shape="round"
                        size="large"
                        ghost="true"
                        >
                            {genr}
                        </Button>
                    </div>
                );
            })}
        </div>
        <div id="transfer">
            <Transfer 
                dataSource={genres["othersGenres"]}
                showSearch="true"
            />
        </div>
    </div>)
}
