import React, { useEffect, useState }  from 'react';
import axios from "axios";
import API_URL from "../conf/api-url";

export function KoreanBookPage(){
    // console.log("hello world")
    // const [genres, setGenres] = useState(null);
    // useEffect(()=>{
    //     axios
    //     // .get(`${API_URL}korean`)
    //     .get(`${API_URL}korean`)
    //     .then((result)=>{
    //         console.log(result);
    //         setGenres(result.data);
    //     })
    //     .catch((error)=>{
    //         console.error(error);
    //     });
    // }, [])

    // if (genres === null){
    //     return <h1>정보를 받고 있습니다...</h1>
    // }


    return(
    <div id="upload-container">
      <Form name="상품 업로드" onFinish={onSubmit}>
        <Form.Item 
          name="upload"
          label={<div className="upload-label">상품 사진</div>}
        >
          <Upload
            name="image"
            action={`${API_URL}/images`}
            listType="picture"
            showUploadList={false}
            onChange={onChangeImage}
          >
            { // if imageUrl is received, view the uploaded image
              imageUrl
              ? <img id="upload-img" src={`${API_URL}/${imageUrl}`}/>
              : (
                <div id="upload-img-placeholder">
                  <img src="/images/icons/camera.png" />
                  <span>이미지를 업로드해주세요.</span>
                </div>
                )
            }
          </Upload>
        </Form.Item>
        <Divider />
        <Form.Item
          name="seller"
          label={<div className="upload-label">판매자 명</div>}
          rules={[{ required: true, message: "판매자 이름을 입력해주세요" }]}
        >
          <Input
            className="upload-name"
            size="large"
            placeholder="이름을 입력해주세요"
          />
        </Form.Item>
        <Divider />
        <Form.Item
          name="name"
          label={<div className="upload-label">상품 이름</div>}
          rules={[{ required: true, message: "상품 이름을 입력해주세요" }]}
        >
          <Input
            className="upload-name"
            size="large"
            placeholder="상품 이름을 입력해주세요"
          />
        </Form.Item>
        <Divider />
        <Form.Item
          name="price"
          label={<div className="upload-label">상품 가격</div>}
          rules={[{ required: true, message: "상품 가격을 입력해주세요" }]}
        >
          <InputNumber
            defaultValue={0}
            className="upload-price"
            size="large"
          />
        </Form.Item>
        <Divider />

        {/* <div id="product-desc-and-submit"> */}
        <Form.Item
          name="description"
          label={<div className="upload-label">상품 소개</div>}
          rules={[{ required: true, message: "상품 소개를 입력해주세요" }]}
        >
          <Input.TextArea
            id="product-description"
            size="large"
            showCount={true}
            maxLength={300}
            placeholder="상품 소개를 적어해주세요"
          />
        </Form.Item>
        <Form.Item>
          <Button 
            id="submit-button"
            size="large"
            htmlType="submit"
          >
            문제 등록하기
          </Button>
        </Form.Item>
        {/* </div> */}

      </Form>
    </div>

    );
}
