import React, { useEffect, useState }  from 'react';
import {Button, Transfer, Form, Divider, Input} from "antd";
import axios from "axios";
import API_URL from "../conf/api-url";
import "./index.css"


export function KoreanBookPage(){
    const [genres, setGenres] = useState(null);
    const [btnActive, setBtnActive] = useState(null);

    useEffect(()=>{
        axios
        .get(`${API_URL}/korean`)
        .then((result)=>{
            setGenres(result.data);
            const temp = result.data["reprGenres"];
            var activeArray = []
            for(let i=0; i<temp.length; i++){
                activeArray.push(false)
            }
            setBtnActive(activeArray);
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])


    function toggleActive(idx){
        if(btnActive[idx] === true){
            console.log("hello",idx);
            let newArr = Array.from(btnActive);
            newArr[idx] = false;
            setBtnActive(newArr);
        }
        else{
            console.log("world",idx);
            let newArr = Array.from(btnActive);
            newArr[idx] = true;
            setBtnActive(newArr);
        }
    };

    const Submit = (values)=>{
        axios.post(`${API_URL}/korean`,{
            selected_genres: values
        })
    };



    if (genres === null){
        return( 
            <div id="loading">
                <Button
                    type="link"
                    loading="true"
                    size="large"
                    style={{fontSize:"250%"}}
                >
                    Loading
                </Button>
            </div>
        )
    }

    return(
        <div>
            <Form name="user profile" onFinish={Submit}>
                <Form.Item>
                    <Input.Search
                        size="large"
                        // addonBefore="읽었던 책이 있다면 추가해주세요!"
                        // loading="true"
                    />
                </Form.Item>
                <Divider/>
                <Form.Item>
                    <div id="mainGenres">
                        {genres["reprGenres"].map((genre, idx)=>{
                            return(
                                <Button
                                    type="primary"
                                    shape="round"
                                    size="large"
                                    ghost="true"
                                    className={"btn"}
                                    style={{
                                        backgroundColor: btnActive[idx] ? '#1890ff' : '',
                                        color: btnActive[idx] ? 'white' : '',
                                    }}
                                    onClick={()=>toggleActive(idx)}
                                >
                                    {genre}
                                </Button>
                            );
                        })}
                    </div>
                </Form.Item>
                <Divider/>
                <Form.Item>
                    <div id="transfer">
                        <Transfer 
                            dataSource={genres["othersGenres"]}
                            showSearch="true"
                        />
                    </div>
                </Form.Item>
            </Form>
        </div>
    )
}
