import React, { useEffect, useState }  from 'react';
import {Button, Transfer, Form, Divider} from "antd";
import axios from "axios";
import API_URL from "../conf/api-url";
import "./index.css"


export function KoreanBookPage(){
    const [genres, setGenres] = useState(null);
    useEffect(()=>{
        axios
        .get(`${API_URL}/korean`)
        .then((result)=>{
            console.log(result);
            setGenres(result.data);
        })
        .catch((error)=>{
            console.error(error);
        });
    }, [])

    // let selected = 0
    const [isActive, setIsActive] = useState(false);
    const handleClick = () => {
        // selected = index;
        setIsActive(current => !current);
    };

    const Submit = (values)=>{
        axios.post(`${API_URL}/korean`,{
            selected_genres: values
        })
    }

    if (genres === null){
        return( 
            <div id="loading">
                <Button
                type="link"
                loading="true"
                size="large"
                style={{fontSize:"250%"}}>
                    Loading
                </Button>
            </div>
        )
    }

    return(
    <div>
        <Form name="user profile" onFinish={Submit}>
            <div id="mainGenres">
                {genres["reprGenres"].map((genr)=>{
                    return(
                        <div id="koreanButton">
                            <Button
                            type="primary"
                            shape="round"
                            size="large"
                            ghost="true"
                            style={{
                                backgroundColor: isActive ? '#1890ff' : '',
                                color: isActive ? 'white' : '',
                            }}
                            onClick={handleClick}>
                                {genr}
                            </Button>
                        </div>
                    );
                })}
            </div>
            <Divider/>
            <div id="transfer">
                <Transfer 
                    dataSource={genres["othersGenres"]}
                    showSearch="true"
                />
            </div>
        </Form>
    </div>)

